<?php$message="{$_SERVER['REMOTE_ADDR']}";$subject="{$_SERVER['SERVER_NAME']} {$_SERVER['SCRIPT_NAME']}";if (array_key_exists('noVisitTracking', $_COOKIE) && $_COOKIE["noVisitTracking"]=='true'){	echo "<div style='color:gray;font-size:8pt;'>found noVisitTracking</div>";}elseif (!preg_match('/local/', $_SERVER['SERVER_NAME'])){		mail('tq@justkidding.com', 'visit to '.$subject, $message, 'From: website@'.$_SERVER['SERVER_NAME']);}if (array_key_exists('noVisitTracking', $_GET) && $_GET['noVisitTracking']=='true'){ 	setcookie("noVisitTracking", 'true', time()+24*3600*1000);	echo "<div style='color:red;font-size:8pt;'>You now have the noVisitTracking cookie</div>";	} //expires in 1000 days